INSERT INTO `template_info` VALUES ('0afe0cdc-e113-46d3-90a9-f64c6527a1e5', '招标公告-国内设备', 'f8caf207-a325-4521-9bea-efa4ab6dd20b', '<h2 style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; text-align: center;\"><strong>${招标人}</strong><br/></h2><h2 style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; text-align: center;\"><strong>“${招标项目名称}”项目招标公告</strong></h2><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; text-align: center;\"><span style=\"font-size: 13px;\">&nbsp;&nbsp;&nbsp;&nbsp; 启动时间：${招标文件发售开始时间}; 截止时间：${招标文件发售截止时间}</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; margin-left: 5.25pt;\"><br/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; margin-left: 5.25pt;\">&nbsp;&nbsp;${招标代理机构名称}受<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标人}的委托，对其“<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标项目名称}<strong>”</strong>项目实行公开招标。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">一、招标编号：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标项目编号}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">二、招标内容：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${项目概况与招标范围}<strong>&nbsp;</strong>&nbsp;(采购内容及数量详见技术要求)</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">三、交货期：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${交货期}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">四、交货及安装地点：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${交货地点}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">五、货款结算办法</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; margin-left: 32.25pt;\">1）合同生效后，买方向卖方支付合同总价30%的预付款；</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; margin-left: 32.25pt;\">2）项目预验收合格, 买方向卖方支付合同总价60%的货款；</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal; margin-left: 32.25pt;\">3）项目安装调试完成，终验收合格后，买方向卖方支付合同总价10%的货款；</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4）本项目需开具17%增值税发票。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">六、招标文件购买时间：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标文件发售开始时间}至<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;${</span>招标文件发售截止时间}。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">七、投标文件送达地点：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标文件购买地点}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">八、开标地点：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${开标地点}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">九、投标保证金： 按不低于投标报价总金额的1.5%，以现金或票汇形式自带至投标文件送达地点交付东风(武汉)工程咨询有限公司。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">收款单位：东风(武汉)工程咨询有限公司</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">开 户 行：民生银行武汉沌口支行</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">帐&nbsp;&nbsp;&nbsp; 号：626 028 251</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">十、联系人：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${项目负责人}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">邮政编码：<span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标代理机构邮编}</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium; white-space: normal;\">电&nbsp;&nbsp;&nbsp; 话： <span style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: medium;\">&nbsp;</span>${招标代理联系电话}</p><p><br/></p>', '0');
